mapper.select:
SELECT RESNUM, RESTITLE, TO_CHAR(REG_DATE, 'YYYY-MM-DD') REG_DATE
FROM PERSONAL_RESUME
WHERE USERID = #{ userid }

SELECT USERNAME, BIRTH, UPHONE, UADDR, EMAIL, GENDER
FROM PERSONAL_MEMBERS
WHERE USERID = #{ userid }

SELECT EDU_NAME
FROM EDU_LIST
WHERE EDU_CODE = #{ edu_code }

SELECT CAREER_NAME
FROM CAREER
WHERE CAREER_CODE = #{ career }

mapper.insert:
INSERT INTO PERSONAL_RESUME(
                             RESNUM, RESTITLE, EDU_CODE
                           , EDU_CONTENT, LICENSE, CAREER_CODE, CAREER_CONTENT, SKILL
                           , SIDO_CODE, GUGUN_CODE, BACKGROUND, MOTIVATION, PERSONALITY
)VALUES('res' || LPAD((SELECT NVL(MAX(TO_NUMBER(SUBSTR(RESNUM, 4))), 0) + 1 FROM PERSONAL_RESUME), 3, '0')
       , #{restitle}, #{edu_code}, #{edu_content}, #{license}, #{career_code}, #{career_content}
       , #{skill}, #{sido_code}, #{gugun_code}, #{background}, #{motivation}, #{personality});

mapper.update:
UPDATE PERSONAL_RESUME
SET PICTURE = #{picture}
WHERE RESNUM = #{resnum}
