mapper.select:
SELECT RESNUM, RESTITLE, TO_CHAR(REG_DATE, 'YYYY-MM-DD') REG_DATE
  FROM PERSONAL_RESUME
  WHERE USERID = #{ userid }

SELECT USERNAME, BIRTH, UPHONE, UADDR, EMAIL
  FROM PERSONAL_MEMBERS
  WHERE USERID = #{ userid }

SELECT EDU_NAME
  FROM EDU_LIST
  WHERE EDU_CODE = #{ edu_code }

SELECT CAREER_NAME
 FROM CAREER
 WHERE CAREER_CODE = #{ career }
